import React, { useState } from 'react';
import './AuthComponent.css';
import signupBackground from'./../../assets/images/tavern-7411977_1920.jpg'
import { Link } from 'react-router-dom';
import SignIn from '../SignIn/SignIn';

function AuthComponent() {

    const [showPassword, setShowPassword] = useState(false);
    const [keepSignIn, setKeepSignIn] = useState(false);

    const handleTogglePassword = () => {
        setShowPassword(!showPassword);
    }

    const handleCheckboxChange = (e) => {
        setKeepSignIn(e.target.checked);
    }

    return (
        <div className='auth-container'>
            <div className="auth-background-panel">
                <img src={signupBackground} alt="Unselectable Image" class="auth-background-img img-unselectable" />
            </div>
            <SignIn />
        </div>
    )
}

export default AuthComponent;
